select t.id,t.userId,t.userName,t.longitude,t.latitude,t.scanTime,t.country,t.province,t.city,t.distance, t.street,t.sematicDescription,t.formattedAddress,t.waresId,t.publicCode,t.privateCode,t.insideCode, u.id u_id, u.username u_username, u.email u_email, u.regtime u_regtime, w.id w_id, w.name w_name, w.creater w_creater, w.awardId w_awardId from scan_record t left join sysuser u on t.userId = u.id left join wares w on t.waresId = w.id where 1=1
${conditionSql}
order by t.scanTime desc
limit #{page.start},#{page.end}

select count(1) from scan_record t where t.publicCode in 
(select a.publicCode from activity a where a.publisherId = #{publisherId})

	${conditionSql}

select t.id,t.userId,t.userName,t.longitude,t.latitude,t.scanTime,t.country,t.province,t.city,t.distance, t.street,t.formattedAddress,t.sematicDescription, u.id u_id, u.username u_username, u.email u_email, u.regtime u_regtime, a.id a_id, a.title a_title, a.description a_description, a.content a_content from scan_record t left join sysuser u on t.userId = u.id left join activity a on t.publicCode = a.publicCode where t.publicCode in (select a.publicCode from activity a where a.publisherId = #{publisherId})
${conditionSql}
order by t.scanTime desc
limit #{page.start},#{page.end}

select t.userId, count(DISTINCT t.waresId) count, t.waresId, t.publicCode, t.privateCode, t.insideCode from scan_record t where 1 = 1
${conditionSql}
group by t.userId, t.waresId
