mapper.update:
update wares
name=#{name}, creater=#{creater}, publicCode=#{publicCode}, privateCode=#{privateCode}, insideCode=#{insideCode}, createTime=#{createTime}, status=#{status}, awardId=#{awardId}
where id = #{id}

mapper.select:
		select count(1) from wares where  1=1

            and name like concat('%',#{name},'%')


            and publicCode = #{publicCode}


            and privateCode = #{privateCode}

		select t.id,t.name,t.creater,t.publicCode,t.privateCode,t.insideCode,t.createTime,t.status,t.awardId from wares t where 1=1

			and t.name like concat('%',#{wares.name},'%')


			and t.creater = #{wares.creater}


			and t.awardId = #{wares.awardId}


            order by #{page.sort} #{page.order}


            limit #{page.start},#{page.end}

