mapper.select:
select u.id,u.username,u.password,u.email,u.gender,u.qq,u.weixin,u.regTime,u.idcard,u.locked t.name sysname, (select name from systemdef psys where psys.id=t.ptopid) psysname from sysuser u,systemdef t where u.sysid=t.id
and (u.sysid=t.id or u.sysid=t.parentid or u.sysid=t.ptopid) and (t.id = #{csysid} or t.parentid = #{csysid} or t.ptopid = #{csysid})
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

		select u.id u_id, u.username u_username, u.email u_email, u.gender u_gender, u.qq u_qq, u.userAlias u_userAlias, u.locked u_locked,
		u.weixin u_weixin, u.regTime u_regtime, u.idcard u_idcard, u.creatorName u_creatorName,
		u.telephone u_telephone, u.address u_address, u.merchant u_merchant, u.loginFrequency u_loginFrequency, u.flagCode u_flagCode,
		r.id r_id, r.name r_name, r.roleDesc r_roleDesc
		from sysuser u left join sysrole r on u.userType = r.id where 1=1 

			${conditionSql}


            order by #{page.sort} #{page.order}


            limit #{page.start},#{page.end}

mapper.update:
update sysuser
username =#{username}, password =#{password}, idcard=#{idcard}, email=#{email}, gender=#{gender}, qq=#{qq}, weixin=#{weixin}, regTime=#{regTime}, userType=#{userType}, generateName=#{generateName}, userAlias=#{userAlias}, creatorId=#{creatorId}, creatorName=#{creatorName}, creatorName=#{creatorName}, loginTime=#{loginTime}, lastLoginTime=#{lastLoginTime}, locked=#{locked}, loginFrequency=#{loginFrequency}, address=#{address}, telephone=#{telephone}, merchant=#{merchant}, flagCode=#{flagCode}, wxOpenId=#{wxOpenId}, points=#{points}, noUsePoints=#{noUsePoints}
where id = #{id}
